COMMAND.ACTION:
if [ "${direct_sub}" == "in" ]; then
	cdbctl update/cdb/interface/${iface}/igs_acl/${acl_name}
else
	cdbctl update/cdb/interface/${iface}/egs_acl/${acl_name}
fi;

    if [ "${direct_sub}" != "both" ]; then
		cdbctl show/cdb/l2/show_acl_counter/normal/${iface_name}/${direct_sub}
	else
		cdbctl show/cdb/l2/show_acl_counter/normal/${iface_name}
	fi;
