COMMAND.ACTION:
            if [ "all" == ${type} ]; then
                cdbctl show/cdb/debug/switch_debug/${detail}
                cdbctl show/cdb/debug/route_debug/${detail}
                cdbctl show/cdb/debug/app_debug/${detail}
                cdbctl show/cdb/debug/ptp_debug/${detail}
                cdbctl show/cdb/debug/quagga_debug/${detail}
