                cdbctl show/cdb/debug/opm_debug/${detail}
                cdbctl show/cdb/l2/show_dot1x/debugging/${detail}
                cdbctl show/cdb/l2/show_sflow/debugging/${detail}
                cdbctl show/cdb/debug/hsrv_debug
		   cdbctl show/cdb/debug/ssm_debug/${detail}
		   ovspid=`pgrep ovs-vswitchd`
	         if [ $ovspid ]; then
	         	cdbctl show/cdb/debug/openflow_debug/${detail}
	         fi
