COMMAND.ACTION:
		if [ ${date_or_rec} == "recurring" ]; then
			 if [ ${start_whichday} == "start_month" ]; then
			 	cdbctl update/cdb/app/tag_summer_clock/${name}/recurring/rec_date/${start_month}/${start_day}/${start_time}/${end_month}/${end_day}/${end_time}/${offset_time}
			 else
			    cdbctl update/cdb/app/tag_summer_clock/${name}/recurring/rec_which/${start_whichday}/${start_weekday}/${start_month}/${start_time}/${end_whichday}/${end_weekday}/${end_month}/${end_time}/${offset_time}
             fi
	    else
			cdbctl update/cdb/app/tag_summer_clock/${name}/date/${date_start_month}/${date_start_day}/${date_start_year}/${date_start_time}/${date_end_month}/${date_end_day}/${date_end_year}/${date_end_time}/${date_offset_time}

	    fi
