COMMAND.ACTION:
case "${mode}" in
	"none")
		cdbctl update/cdb/sys/port_stats/
		cdbctl update/cdb/sys/clear_if_stats_rate/
		cdbctl show/cdb/sys/show_management_if/clear_counters
	;;

	"iface_name")
		cdbctl update/cdb/sys/port_stats/${iface_name}
		cdbctl update/cdb/sys/clear_if_stats_rate/${iface_name}
	;;

	"mgmt-if")
		cdbctl show/cdb/sys/show_management_if/clear_counters
	;;
esac

if [ "input-rate" == ${mode} ]; then
	cdbctl update/cdb/sys/port_stats_rate/${iface}/input_rate_threshold/${bandwidth_usage}/input_rate_resume/${resume_usage}
elif [ "output-rate" == ${mode} ]; then
	cdbctl update/cdb/sys/port_stats_rate/${iface}/output_rate_threshold/${bandwidth_usage}/output_rate_resume/${resume_usage}
elif [ "output-discard" == ${mode} ]; then
	cdbctl update/cdb/sys/port_stats_rate/${iface}/output_discard_threshold/${discard_threshold}/output_discard_interval/${interval_value}
fi
