COMMAND.ACTION:
if [ ${part} == "mac" ]; then
    cdbctl update/cdb/ptp/ptp_port/${iface}/port_addr/protocol/${part}
elif [ ${part} == "udp" ]; then
    if [ ${part1} == "all" ]; then
        cdbctl update/cdb/ptp/ptp_port/${iface}/port_addr/protocol/${part}/udp_source_ip/${udp_source_ip}
    else
        cdbctl update/cdb/ptp/ptp_port/${iface}/port_addr/protocol/${part}/udp_source_ip/${udp_source_ip}/unicast_dest_ip/${unicast_dest_ip}
    fi    
fi
